#!/bin/bash

# libvec.a is generated by GNU

# use our linker
python ../../main.py multvec.o addvec.o -l libvec.a -e main.out
# echo "Result via our linker:"
# ./main.out
echo ""

# use GNU
ar rcs libvec-gnu.a addvec.o multvec.o 
gcc -static -o main-gnu.out main.o libvec-gnu.a
echo "Result via GNU:"
./main-gnu.out
echo ""
